// Helper functions for LGBTQIA+ specific data
export const getIdentitySpecificScore = (cityData, identity) => {
  return cityData.identitySpecificSafety?.[identity]?.score || null
}

export const getIntersectionalScore = (cityData, intersection) => {
  return cityData.intersectionalCombinations?.[intersection]?.score || null
}

export const getFamilyIntersectionData = (cityData, familyType) => {
  return cityData.familyIntersections?.[familyType] || null
}
